import {useState} from "react";
const Authentication = {
    User : function (){//This is where the token will be validated
        //const [token, setToken] = useToken();
        let token= "dsfdsfsdf";
        //=============== Initial Validation ===================

        //=============== End of intial validation =============
        /*const checkJWT = async () => {
            
            let valid = await validateJWT(token);
            console.log("Valid: "+valid)
            return valid
        }*/
        const a = async() => {
          return await validateJWT(token);
        }
        console.log(a(token));
        
        //p.then((v) => {return v})
        //return false;
        
        return true; //true if valid, false if invalid.
    }
}
export default Authentication;

async function validateJWT(token){
    return fetch("http://localhost:8000/api/v1/authorise_user/", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(token),
  }).then((data) => data.json());
}
