import { Link, Navigate, useNavigate } from "react-router-dom";
import { useState } from "react";
import useToken from "../Hooks/useToken.js";
import "./SignUp.css";
import { ValidateInputs } from "../ExternalClasses/InputValidationClass";
export default function SignUp({ setToken }) {
  const [firstNameValid, setFirstName] = useState();
  const [surnameValid, setSurnameValid] = useState();
  const [emailValid, setEmailValid] = useState();
  const [usernameValid, setUsernameValid] = useState();
  const [passwordValid, setPasswordValid] = useState();

  let VI = ValidateInputs();

  const HandleSubmit = async function () {
    let errors = VI.allInputs();
    if (errors.length == 0) {
      try {
        //POST
        await fetch("http://localhost:8000/api/v1/user/", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            firstName: VI.firstName,
            surname: VI.surname,
            email: VI.email,
            username: VI.username,
            password: VI.password,
          }),
        })
          .then((data) => data.json())
          .then((response) => console.log(response));
      } catch (e) {}
    }
  };

  /*async () => {
    fetch("http://localhost:8000/api/v1/user/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        firstName: firstName,
        surname: surname,
        email: email,
        username: username,
        password: password,
      }),
    })
      .then((data) => data.json())
      .then((response) => {});
  };*/
  return (
    <>
      <div className="container">
        <form id="signUpForm" onSubmit={HandleSubmit} action="#">
          <div className="row">
            <label htmlFor="firstNameInput" className="form-label col-sm-2">
              First Name
            </label>
            <div className="col-sm-4">
              <input
                type="text"
                className="form-control"
                placeholder="e.g. Robert"
                id="firstNameInput"
                onChange={(e) => {}}
              />
            </div>
            <label htmlFor="surnameInput" className="form-label col-sm-2">
              Surname
            </label>
            <div className="col-sm-4">
              <input
                type="text"
                className="form-control"
                placeholder="e.g. Lucas"
                id="surnameInput"
                onChange={(e) => {}}
              />
            </div>
          </div>

          <div className="row">
            <label htmlFor="emailInput" className="form-label col-sm-2">
              Email
            </label>
            <div className="col-sm-10">
              <input
                type="text"
                className="form-control"
                placeholder="email@example.com"
                id="emailInput"
                onChange={(e) => {}}
              />
            </div>
          </div>
          <div className="row">
            <label htmlFor="usernameInput" className="form-label col-sm-2">
              Username
            </label>
            <div className="col-sm-10">
              <input
                type="text"
                className="form-control"
                placeholder="Username"
                id="usernameInput"
                onChange={(e) => {}}
              />
            </div>
          </div>
          <div className="row">
            <label htmlFor="passwordInput" className="form-label col-sm-2">
              Password
            </label>
            <div className="col">
              <input
                type="password"
                className="form-control"
                placeholder="Password"
                id="passwordInput"
                onChange={(e) => {}}
              />
            </div>
          </div>
          <div className="row">
            <div className="col-3" />
            <input
              type="submit"
              value="Submit"
              className="btn btn-outline-success col-sm-6 center"
            />
          </div>
        </form>
        <br />
        <p className="text-center">
          Already have an account? sign in <Link to="/login">here.</Link>
        </p>
      </div>
    </>
  );
}
