import { Navigate, Outlet } from "react-router-dom";
import { useState, useEffect } from "react";
import useToken from "../Hooks/useToken.js";
import Authentication from "../Services/Authentication";
import React from "react";
import { Login } from "./PageImports.js";
function getToken() {
  //gets the value of token from session storage.
  try {
    return JSON.parse(sessionStorage.getItem("token"));
  } catch (e) {
    sessionStorage.removeItem("token");
  }
}

function PrivateOutlet() {
  const [userLoggedIn, setUserLoggedIn] = useState(False);
  /*
  if (pageState === "loading") {
    Authentication.User(getToken(), setPageState);
  }*/
  useEffect(()=>{//executes after render.
    Authentication.User(getToken(), setUserLoggedIn)
  });
  /*
  if (pageState === "loading") {
    return <div>loading</div>;
  }*/
  console.log(pageState);
  return userLoggedIn? (
    <Outlet/>
  ) : (
    <Login setPageState={setUserLoggedIn} />
  );
}
export default PrivateOutlet;
